use std::sync::Arc;

use futures_util::future;
use tg::utils;
use tokio::{io::AsyncWriteExt, net::TcpStream};

async fn work(host: Arc<String>) {
    let mut cli = TcpStream::connect(&*host).await.unwrap();

    let data = b"";

    let (_, mut writer) = cli.split();
    for i in 0..10000 {
        let req = tg::nw::packet::Packet::with(1, i + 1, data);
        if let Err(err) = writer.write_all(req.raw()).await {
            println!("write failed: {:?}", err);
            break;
        }
    }
}

#[tokio::main]
async fn main() {
    let mut arr = Vec::new();
    let host = Arc::new(std::env::args().nth(1).unwrap());
    let beg = utils::now_unix_micros();
    for _ in 0..100 {
        let v = host.clone();
        arr.push(tokio::spawn(async move {
            work(v).await;
        }));
    }

    future::join_all(arr).await;
    println!(
        "done....!!!\n 总耗时: {} micro seconds",
        utils::now_unix_micros() - beg
    );
}
